import React from "react"
import { useAuth } from "../contexts/AuthContext"
import Sidebar from "./Sidebar"
import Split from "react-split"

export default function Dashboard(props) {
    const quizzesData = {
        "quizzes": [
            {
                "id": 0,
                "title": "This is my quiz",
                "questions": [
                    {
                        "question": "Why?",
                        "answers": [
                            "Cause yes", "Why not", "No", "Yes"
                        ],
                        "correctAnswer": "Yes"
                    }
                ]
            }
        ]
    }

    const {currentUser} = useAuth()

    const things = quizzesData.quizzes.map(quiz => {
        return <div className="quiz-sidebar-name" key={quiz.id}>{quiz.title}</div>
    })

    return (

        <main>
            {
                quizzesData.quizzes.length > 0
                ?
                <Split sizes={[30, 70]} direction="horizontal" className="split">
                    <div className="sidebar">
                        <div className="quiz-preview">{things}</div>
                    </div>
                </Split>
                :
                <div className="no-quizzes">
                    <h1>You have no quizzes yet</h1>
                    <button className="create-first-quiz">Create one now!</button>
                </div>
            }
        </main>
    )
}
