import React, {useState} from 'react'

export default function Browse() {

    const [resultsAppearing, setResultsAppearing] = useState(false)

    function searchToggle(obj, evt){
        var container = obj.closest('.search-wrapper');
            if(!container.hasClass('active')){
                container.addClass('active');
                evt.preventDefault();
            }
            else if(container.hasClass('active') && obj.closest('.input-holder').length == 0){
                container.removeClass('active');
                // clear input
                container.find('.search-input').val('');
            }
    }


    return (
        <div>
            <div className="browse-rect">
                <input 
                    className="browse-input"
                    name="browse"
                    placeholder="                   Search for quizzes"
                />

                <div className="search-wrapper">
                    <div className="input-holder">
                        <input type="text" class="search-input" placeholder="Type to search" />
                        <button className="search-icon" onClick={searchToggle}><span></span></button>
                    </div>
                    <span className="close" onClick={searchToggle}></span>
                </div>

            { !resultsAppearing && <div className="res-hint">Results will appear here</div> }

            </div>
        </div>
    )
}
