import React, {useState} from "react"

export default function RegisterPage() {

    const [formData, setFormData] = useState(
        {
            username: "",
            password: "",
            confirmPassword: ""
        }
    )

    function handleChange(event) {
        const {name, value} = event.target
        setFormData(prevFormData => {
            return {
                ...prevFormData,
                [name]: value
            }
        })
    }

    function handleSubmit(event) {

        if (formData.username !== "" && formData.password !== "" && formData.confirmPassword !== "") {
            event.preventDefault();
        } else {
            alert("The fields can't be empty!")
        }
    }
    

    return (
        <div className="register-rect">
            <div className="register-title">Register</div>
            <form>
                <input className="usernameReg-field"
                    type="text"
                    name="username" 
                    placeholder="Username" 
                    required
                    />

                <input
                    className="passwordReg-field"
                    type="password"
                    name="password"
                    placeholder="Password"
                    required
                    />

                <input 
                    className="password-confirm"
                    type="password" 
                    name="confirmPassword" 
                    placeholder="Confirm Password" 
                    required
                    />

                <div className="register-button" onClick={registerClicked}>Register</div>
            </form>
            
            <div className="go-to-login">I already have an account</div>
        </div>
    )

    
}
