import React, {useState, forwardRef, useImperativeHandle, useRef} from "react";
import { useAuth } from "../contexts/AuthContext";
import Navbar from "./Navbar";
import Browse from "./dashboard/Browse";
import MyQuizzes from "./dashboard/MyQuizzes";
import CreateQuiz from "./dashboard/CreateQuiz";
//import Modal from "./dashboard/Modal";
import { motion, AnimatePresence } from "framer-motion"

export default function Dashboard(props) {
  const { currentUser } = useAuth();

  const modalRef = useRef()
  const [openModal, setOpenModal] = useState(false)
  const [effect, setEffect] = useState(false)

  
  //() => setOpenModal(true)

  
  return (
    <> 
      <Modal ref={modalRef}>Hello</Modal>

      {/* { openModal && <Modal closeModal={setOpenModal} effect={effect} />} */}
      <Navbar />
      <div name="container" className="h-[calc(100%-80px)] flex">

        <div className="w-[50%] justify-center items-center flex border-r-[6px] border-r-special-black">
          <Browse />
        </div>
        <div className="w-[50%] justify-between flex flex-col">
          <div className="h-[50%] border-b-[6px] border-b-special-black flex flex-col justify-center items-center">
            <div className="text-[50px] mb-[20px] text-center">My Quizzes</div>
            <MyQuizzes />
          </div>

          <div className="h-[50%] flex flex-col items-center justify-center">
            <div className="text-[50px] mb-[100px]">Create a quiz</div>
            <button onClick={() => modalRef.current.open()} className="flex text-white text-center min-w-[50px] min-h-[50px] justify-center text-[50px] rounded-2xl bg-special-black hover:bg-special-hover w-[18%] h-[18%]">+</button>
          </div>          
        </div>
      </div>
    </>
  );
}

const Modal = forwardRef((props, ref) => {
  const [open, setOpen] = useState(false);

  useImperativeHandle(ref, () => {
    return {
      open: () => setOpen(true),
      close: () => setOpen(false)
    };
  });

  return (
    <AnimatePresence>
      {open && (
        <>
          <motion.div
            initial={{
              opacity: 0
            }}
            animate={{
              opacity: 1,
              transition: {
                duration: 0.3
              }
            }}
            exit={{
              opacity: 0,
              transition: {
                delay: 0.3
              }
            }}
            onClick={() => setOpen(false)}
            className="w-[100vw] h-[100vh] top-0 left-0 fixed flex justify-center items-center" name="modal-bg"
          />
          <motion.div
            initial={{
              scale: 0
            }}
            animate={{
              scale: 1,
              transition: {
                duration: 0.3
              }
            }}
            exit={{
              scale: 0,
              transition: {
                delay: 0.3
              }
            }}
            className="w-[500px] h-[500px] bg-[#0e0f0c] fixed top-[50%] left-[50%] bottom-[50%] right-[50%] flex flex-col rounded-xl"
          >
            <motion.div
              className="modal-content"
              initial={{
                x: 100,
                opacity: 0
              }}
              animate={{
                x: 0,
                opacity: 1,
                transition: {
                  delay: 0.3,
                  duration: 0.3
                }
              }}
              exit={{
                x: 100,
                opacity: 0,
                transition: {
                  duration: 0.3
                }
              }}
            >
              {props.children}
            </motion.div>
          </motion.div>
        </>
      )}
    </AnimatePresence>
  );
});
