import React, {useState} from 'react'

export default function QuizQuestionsPreview() {


    const [formData, setFormData] =  useState(
        {
            answer: ""
        }
    )


    function handleChange(event) {
        const {value, checked, name} = event.target

        setFormData(prevFormData => {
            return {
                ...prevFormData,
                [name]: checked
            }
        })
    }

    return (
        <div className="quiz-preview-rect">
            <div className="quiz-preview-title">Quiz Preview</div>

            <div className="question-preview">1. What is the radius of a fuckin circle?</div>

            <form>
                <fieldset>
                    <input
                        type="radio"
                        name="answer"
                        id="answer1"
                        value="answer1"
                        checked={formData.answer === "answer1"}
                        onChange={handleChange}
                    />

                    <input
                        type="radio"
                        name="answer"
                        id="answer2"
                        value="answer2"
                        checked={formData.answer === "answer2"}
                        onChange={handleChange}
                    />

                    <input
                        type="radio"
                        name="answer"
                        id="answer3"
                        value="answer3"
                        checked={formData.answer === "answer3"}
                        onChange={handleChange}
                    />

                    <input
                        type="radio"
                        name="answer"
                        id="answer4"
                        value="answer4"
                        checked={formData.answer === "answer4"}
                        onChange={handleChange}
                    />
                </fieldset>
                
            </form>

        </div>
  )
}
