import React, {useState} from 'react'

export default function QuizQuestionsPreview() {


    const [formData, setFormData] =  useState(
        {
            hey: "",
            answer: ""
        }
    )


    function handleChange(event) {
        const {value, name} = event.target

        setFormData(prevFormData => {
            return {
                ...prevFormData,
                [name]:  value
            }
        })
    }

    return (
        <div className="quiz-preview-rect">
            <div className="quiz-preview-title">Quiz Preview</div>

            <div className="question-preview">1. What is the radius of a fuckin circle?</div>

            <form>
                <input
                    className="answer1-radio"
                    type="radio"
                    name="answer"
                    id="answer1"
                    value="answer1"
                    checked={formData.answer === "answer1"}
                    onChange={handleChange}
                />
                <label className="label-for-answer1" htmlFor="answer1">Why the fuck not?</label>
                <br/>

                <input
                    className="answer2-radio"
                    type="radio"
                    name="answer"
                    id="answer2"
                    value="answer2"
                    checked={formData.answer === "answer2"}
                    onChange={handleChange}
                />
                <label className="label-for-answer2" htmlFor="answer2">Yes</label>
                <br/>

                <input
                    className="answer3-radio"
                    type="radio"
                    name="answer"
                    id="answer3"
                    value="answer3"
                    checked={formData.answer === "answer3"}
                    onChange={handleChange}
                />
                <label className="label-for-answer3" htmlFor="answer3">No, because that's why</label>
                <br/>

                <input
                    className="answer4-radio"
                    type="radio"
                    name="answer"
                    id="answer4"
                    value="answer4"
                    checked={formData.answer === "answer4"}
                    onChange={handleChange}
                />
                <label className="label-for-answer4" htmlFor="answer4">AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA</label>
            </form>

        </div>
  )
}
