import React, {useState} from "react";
import { useAuth } from "../contexts/AuthContext";
import Navbar from "./Navbar";
import Browse from "./dashboard/Browse";
import MyQuizzes from "./dashboard/MyQuizzes";
import CreateQuiz from "./dashboard/CreateQuiz";

export default function Dashboard(props) {
  const { currentUser } = useAuth();

  const [popup, setPopup] = useState(false)

  const openModalButtons = document.querySelectorAll('[data-modal-target]')
  const closeModalButtons = document.querySelectorAll('[data-close-button]')
  const overlay = document.getElementById('overlay')

  openModalButtons.forEach(button => {
    button.addEventListener(('click', () => {
      const modal = document.querySelector(button.dataset.modalTarget)
      openModal(modal)
    }))
  })

  closeModalButtons.forEach(button => {
    button.addEventListener(('click', () => {
      const modal = button.closest('.modal')
      closeModal(modal)
    }))
  })

  function openModal() {
    if (modal == null) return
    modal.classList.add('active')
    overlay.classList.add('active')
  }

  function closeModal() {
    if (modal == null) return
    modal.classList.remove('active')
    overlay.classList.remove('active')
  }

  return (
    <>
      <div className="fixed top-[20%] left-[25%] translate-x-2/4 translate-y-2/4 z-10 bg-special-black w-[500px] max-w-[80%] h-[300px] scale-0 ease-in-out duration-200" id="modal">
        <div name="header" className="flex justify-between items-center border-b-1 border-b-white pt-[10px] pb-[10px]">
          <div name="title" className="text-xl">Example modal text</div>
          <button data-close-button className="bg-transparent text-xl font-bold">&times;</button>
        </div>
        <div name="body" className="pt-[15px] pb-[10px]">Loremdasdasdsaddddddddddddddddddddddddddddddddddddddddddddddddddddddddd</div>
      </div> 
      <Navbar />
      <div name="container" className="h-[calc(100%-80px)] flex">

        <div className="w-[50%] justify-center items-center flex border-r-[6px] border-r-special-black">
          <Browse />
        </div>
        <div className="w-[50%] justify-between flex flex-col">
          <div className="h-[50%] border-b-[6px] border-b-special-black flex justify-center items-center">
            <span>QUIZZESSS</span>
          </div>

          <div className="h-[50%] flex flex-col items-center justify-center">
            <button data-modal-target="#modal" className="flex text-white text-center justify-center text-[50px] rounded-2xl bg-special-black w-[18%] h-[18%]">+</button>
          </div>          
        </div>
      </div>
      <div id="overlay" className="fixed opacity-0 top-0 left-0 right-0 ease-in-out duration-200 bottom-0 bg-[#141414] pointer-events-none"></div>
    </>
  );
}
