import React, {useState} from "react";
import {Link, useNavigate} from "react-router-dom"
import { useAuth } from "../../../contexts/AuthContext";

export default function LoginPage() {

    const [formData, setFormData] = useState(
        {
            email: "",
            password: ""
        }
    )

    const { login } = useAuth()
    const [error, setError] = useState("")
    const [loading, setLoading] = useState(false)
    const navigate = useNavigate()


    function handleChange(event) {
        const {name, value} = event.target
        setFormData(prevFormData => {
            return {
                ...prevFormData,
                [name]: value
            }
        })
    }

    async function handleSubmit(e) {
        e.preventDefault()
        
        try {
            setError("")
            setLoading(true)
            await login(formData.email, formData.password)
            
            navigate("/")
        } catch {
            setError("Failed to log in")
        }

        setLoading(false)
    }

  return (
        <>
            <div name="container" className="flex h-full">
                <div className="flex w-[50%] h-full bg-special-pink items-center justify-center">
                    <div className="flex text-[150px]">Quizzy</div>
                </div>

                <div className="flex w-[50%] h-full bg-special-black">
                    <div className="flex text-[36px] text-white">Login</div>


                </div>
            </div>
